ing = newFiber;\n    }\n\n    prevSibling = newFiber;\n    index++;\n  }\n  // \u8FD4\u56DE\u4E0B\u4E00\u4E2A\u4EFB\u52A1\u5355\u5143\uFF08fiber\uFF09\n  // \u6709\u5B50\u8282\u70B9\u76F4\u63A5\u8FD4\u56DE\n  if (fiber.child) {\n    return fiber.child;\n  }\n  // \u6CA1\u6709\u5B50\u8282\u70B9\u5219\u627E\u5144\u5F1F\u8282\u70B9\uFF0C\u5144\u5F1F\u8282\u70B9\u4E5F\u6CA1\u6709\u627E\u7236\u8282\u70B9\u7684\u5144\u5F1F\u8282\u70B9\uFF0C\n  // \u5FAA\u73AF\u904D\u5386\u76F4\u81F3\u627E\u5230\u4E3A\u6B62\n  let nextFiber = fiber;\n  while (nextFiber) {\n    if (nextFiber.sibling) {\n      return nextFiber.sibling;\n    }\n    nextFiber = nextFiber.parent;\n  }\n  return null;\n}\n")), mdx("hr", null), mdx("h2", {
